sampleQueries.query:
{{graphQueriesTableName}}
 | where Severity_s == "VERY_HIGH" and Status_s == "COMPLETED"
 | order by CompletionTimestamp_t desc

{{graphQueriesTableName}}
 | summarize count() by bin(CompletionTimestamp_t, 1d)
 | render timechart

dataTypes.lastDataReceivedQuery:
{{graphQueriesTableName}}
 | where TimeGenerated > ago(12h) | summarize Time = max(TimeGenerated)
 | where isnotempty(Time)
